import { selector } from "recoil";
import { themeState } from "./atoms";

export const changeMode = selector({
    key: 'changeTheme',
    get: async ({ get }) => {
        //const localTheme = localStorage.getItem('DARK_MODE')
        const isModeDark = !get(themeState)
        await localStorage.setItem('DARK_MODE', isModeDark.toString());
        return isModeDark
    },
    set: ({set}, newValue) => {
        localStorage.setItem('DARK_MODE', newValue.toString());

        set(themeState, newValue)
    }
})
