        //     const post = response.data
        //     return { post }
        // }
    }

    useEffect(function mount() {
        const track = document.querySelector('#track');
        const waveform = WaveSurfer.create({
            barWidth: 3,
            cursorWidth: 1,
            container: '#waveform',
            backend: 'WebAudio',
            height: 80,
            progressColor: '#2D5BFF',
            responsive: true,
            waveColor: '#EFEFEF',
            cursorColor: 'transparent',
        });

        waveform.load(track);
    }, [])

