import styled from 'styled-components';

export const Button = styled.button`
    width: 100%;
    padding: 16px;
    margin-top: 16px;
    border: none;
    border-radius: 16px;

    &:hover {
        opacity: 0.5;
        cursor: pointer;
    }
`;

export const ButtonPrimary = styled(Button)`
    background-color: ${({ theme }) => theme.colors.primaryColor};
    color: ${({ theme }) => theme.colors.textColor}; 

`
