import React, { useEffect } from 'react'
import styled from 'styled-components';
import { Button, ButtonComment, ButtonDesLike, ButtonLike, ButtonPrimary } from '../../components/Button';
import Main from '../../components/layout/Main'
import { useRouter } from 'next/router'
import { API } from '../../services/api';
import { AiFillDislike, AiFillLike } from 'react-icons/ai';
import { FaCommentDots } from 'react-icons/fa'

import WaveSurfer from 'wavesurfer.js';


function Audio() {
    const router = useRouter()
    const url = "https://api.zapaudios.com/uploads/chegou_o_ingresso_752dee0033.mp3"

    async function initData() {
        // const response = await API.get('/post')
        // if (response.status === 200 && response.data) {
        //     const post = response.data
        //     return { post }
        // }
    }

    let waveform

    if (window !== undefined) {
        const track = document.querySelector('#track');
        waveform = WaveSurfer.create({
            barWidth: 3,
            cursorWidth: 1,
            container: '#waveform',
            backend: 'WebAudio',
            height: 80,
            progressColor: '#2D5BFF',
            responsive: true,
            waveColor: '#EFEFEF',
            cursorColor: 'transparent',
        });

        waveform.load(track);
    }

    const handlerPlay = () => {
        waveform.playPause();
    }

    return (
        <Main>
            <Article>
                <TitlePage>Lorem Ipsum is simply dummy text of the printing and typesetting industry.</TitlePage>
                <Paragraph>
                    Lorem Ipsum is simply dummy text of the printing and typesetting industry.
                    Lorem Ipsum has been the industry standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book.
                    It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged.
                </Paragraph>

                <Wave id="waveform" />
                <audio id="track" src={url} />

                <ContainerActions>
                    <ButtonLike>
                        <AiFillLike />
                    </ButtonLike>

                    <ButtonDesLike>
                        <AiFillDislike />
                    </ButtonDesLike>

                    <ButtonComment>
                        <FaCommentDots />
                    </ButtonComment>

                </ContainerActions>
                <Button onClick={handlerPlay}> Play </Button>
                <ButtonPrimary onClick={() => router.back()}>Back</ButtonPrimary>
            </Article>
        </Main>
    )
}

export default Audio

export const Article = styled.article`

`;

const TitlePage = styled.h1`
  margin-bottom: 16px;
  font-weight: bold;
  font-size: 18px;
`;

const Paragraph = styled.p`
  font-size: 14px;
  color: ${({ theme }) => theme.colors.secundaryTextColor};
  opacity: 0.7;
`;

const ContainerActions = styled.div`
  flex: 1;
  height: 60px;
  display: flex;
  align-items: center;
  margin: 16px 0px;
`;

export const Wave = styled.div`
  width: 100%;
  height: 90px;
`;
