import { useRecoilValue, useSetRecoilState } from "recoil";
import { themeState } from "../recoils/atoms";

export const useTheme = () => ({
    isDarkMode: useRecoilValue(themeState),
    changeMode : () => { localStorage.setItem('DARK_MODE', useRecoilValue(themeState).toString()); useSetRecoilState(changeMode)},
})
