import React, { useEffect, useRef, useState } from 'react'
import styled from 'styled-components';
import { Button } from '../../components/Button';
import Main from '../../components/layout/Main'
import { useRouter } from 'next/router'
import dynamic from "next/dynamic";
import { API } from '../../services/api';
import { PostObject } from '../../components/Card/Card';
const Waveform = dynamic(() => import('../../components/WaveForm'), { ssr: false, });

function Audio() {
    const router = useRouter()
    const url = "https://api.zapaudios.com/uploads/chegou_o_ingresso_752dee0033.mp3"
    const [posts, setPost] = useState<PostObject[]>()
    const { slug } = router.query

    async function initData() {
        const response = await API.get(`/posts?slugAudio=${slug}`)
        if (response.status === 200 && response.data) {
            setPost(response.data)
        }
    }

    useEffect(() => {
        initData()
    })

    return (
        <Main>
            <Article>
                {posts.length > 0 && posts.map( post => (
                    <>
                        <TitlePage>{ post.titleAudio }</TitlePage>
                        <Paragraph>
                            { post.descAudio }Â 
                        </Paragraph>

                        <Waveform url={url} />

                        <Button onClick={() => router.back()}>Voltar</Button>
                    </>
                ))}
            </Article>
        </Main>
    )
}

export default Audio

export const Article = styled.article`

`;

const TitlePage = styled.h1`
  margin-bottom: 16px;
  font-weight: bold;
  font-size: 18px;
`;

const Paragraph = styled.p`
  font-size: 14px;
  color: ${({ theme }) => theme.colors.secundaryTextColor};
  opacity: 0.7;
`;
