import { selector } from "recoil";
import { themeState } from "./atoms";

export const changeMode = selector({
    key: 'changeTheme',
    get: () => {
        const localTheme = localStorage.getItem('DARK_MODE')
        return localTheme === 'true'
    },
    // set: ({set}, newValue) => {
